import React, { useState } from 'react';
import PropTypes from 'prop-types';
import styles from './Pagination.module.css';

function Pagination(props) {

const [page, setPage] = useState(props);

  function handleAdd(e) {
    setPage(page => page + 1)
    return page;
    console.log(page)
}

function handleMinus(e) {
  if (page !== 1) {
    setPage(page => page - 1)
    return page;
  }
  }


return < div className = {styles.pageBtn}>
    < button className= {styles.button} onClick={ props.pages({handleMinus}) } > Previous </ button >
    < button className={styles.button} onClick= {props.pages({handleAdd})} > Next </ button>
    < /div >
}

Pagination.propTypes = {
  pages: PropTypes.func
}


export default Pagination;
