import React, { useState } from 'react';
import Unsplash, { toJson } from 'unsplash-js';
// import Axios from 'axios';
// import DisplayImage from "../displayImage/DisplayImage";
import styles from './Grid.module.css'

 function Grid(){
  const [query, setQuery] = useState("");
  const [image, setImage] = useState([]);

  const unsplash = new Unsplash({
     accessKey: "-ykMZOxHVnu9CGPy0hKbcnbp12CFAZptKJ5CfeOYoiM"
   });

   async function searchImages(e) {
     e.preventDefault();

     unsplash.search.photos(query).then(toJson).then((json) => {
       setImage(json.results);
     })
      }


    return (<section className = {styles.container} >
    <form className={styles.input}>
    <label htmlFor="query"> {" "} </label>
    < input className = { styles.input } type = "text"
       placeholder = "Search..." name="query" value={query}
       onChange={(e) => setQuery(e.target.value)}/>
    < button className = { styles.button } type = "submit"
    onSubmit={ searchImages } > Search < /button >
    < div className = { styles.grid_3_columns } >
    { image.map(item => { <div className="card" key = { item.id } >
        <img className={styles.img} alt={item.alt_description}
        src={item.urls.full} width="50%" height="50%" />
        < /div > })}
        < /div >
      </form>
    </section>
  );
 }

export default Grid;

