import React from 'react';
import Axios from 'axios';
import Card from './Card';
import './Grid.module.css'

function Grid() {

const [inputText, setInput] = React.useState("");
const [post, setPost] = React.useState([]);
const [image, setImage] = React.useState();

function handleChange(e) {
  console.log(e.target.value)
  setInput(e.target.value);
}

function sendInput(e) {
  e.preventDefault();

try {
  Axios.post("http://localhost:8000/sendInput", {
    inputText
  });
  Axios.get("http://localhost:3000/getImagesArray").then(response => {
      setPost(response.data.imagesArray);
  }, []);
} catch (error) {
  console.log(error.response.data);
}
}

function sendImage(e) {
  setImage(e.target);
}

return <form onSubmit={handleChange}>
<div className="searchBar">
<input type="text" onChange={handleChange} placeholder="Search..."
value={inputText}/>
<button type="submit" onClick={sendInput}>Search</button>
</div>
<div className = "grid-3-columns">
{post.map(url => {
  return <div key = {url.id} >
    <div>
    < img src = {url} alt = "images" onClick={sendImage} />
    </div>
    </div>
  })}
    </div>
    <Card imageProp={image}/>
</form>
}

export default Grid;
