import React, { useState } from 'react';
import Axios from 'axios';
import DisplayImage from "../displayImage/DisplayImage";
import styles from './Grid.module.css'

 function Grid(){
  const [query, setQuery] = useState("");
  const [array, setArray] = useState([]);
  const [perPage, setPage] = useState("1");

  const [image, setImage] = useState("");


  const clientId = "-ykMZOxHVnu9CGPy0hKbcnbp12CFAZptKJ5CfeOYoiM";
  const url = `https://api.unsplash.com/search/photos?client_id=${clientId}&query=${query}&page=1&per_page=${perPage}`

  function getData(e) {
    e.preventDefault();

    try {
      Axios.get(url).then(response => {
        setArray(response.data.results)
        console.log(array)
      }, [query]);
    } catch (error) {
        console.log(error);
      }
    }

  function sendImage(e) {
      setImage(e.target.src);
    }

  function handleClick(e) {


    setPage(e.target)
  }


    return (<section className = {styles.container} >
    < div className = { styles.grid_2_columns } >
    < DisplayImage imageTarget = { image } onChange={ sendImage }/>
    <form className={styles.form}>
    <label htmlFor="query"> {" "} </label>
    < input className = { styles.input } type = "text"
       placeholder = "Search..." name="query" value={query}
       onChange={(e) => setQuery(e.target.value)}/>
    < button className = { styles.button } type = "submit"
    onClick={ getData } > Search < /button >

    < div className = { styles.grid_3_columns } >
    { array.map(url => { return <div key = { url.id } >
       < div >
       < img src = { url.urls.full } alt = "images" onClick = { sendImage }
       className = { styles.img } />
       < /div >
       </div > })}
    < /div >

    < div className = {styles.loadMoreBtn}>
    < button className= {styles.button} > Previous </ button >
    < button className={styles.button}> Next </ button>
    < /div >

      </form>
      </ div>
    </section>
  );
 }

export default Grid;
