    <section className={styles.login}>
      <ul className={styles.ul}>
        <li className={styles.title}>login</li>
        <li className={styles.li}>
          <button className={styles.btn} onClick={onLogin}>
            Google
          </button>
        </li>
        <li className={styles.li}>
          <button className={styles.btn} onClick={onLogin}>
            Github
          </button>
        </li>
      </ul>
    </section>
  );
};

Login.propTypes = {
  authService: PropTypes.object,
};

export default Login;
