import Axios from 'axios';
import DisplayImage from "../displayImage/DisplayImage";
import styles from './Grid.module.css'

 function Grid(){
  const [query, setQuery] = useState("");
  const [array, setArray] = useState([]);
  const [image, setImage] = useState("");

  const clientId = "-ykMZOxHVnu9CGPy0hKbcnbp12CFAZptKJ5CfeOYoiM";
  const url = `https://api.unsplash.com/search/photos?client_id=${clientId}&query=${query}&page=1`

  function getData(e) {
    e.preventDefault();


    try {
      Axios.get(url).then(response => {
        setArray(response.data)
        console.log(array)
        console.log(query)
      }, [query]);
    } catch (error) {
        console.log(error);
      }
    }



  // useEffect(() => {
  //   getData();
  // }, [query]);

  function sendImage(e) {
      setImage(e.target.src);
    }

    return (<section className = {styles.container} >
    < div className = { styles.grid_2_columns } >
    < DisplayImage imageTarget = { image } onChange={ sendImage }/>
    <form className={styles.form}>
    <label htmlFor="query"> {" "} </label>
    < input className = { styles.input } type = "text"
       placeholder = "Search..." name="query" value={query}
       onChange={(e) => setQuery(e.target.value)}/>
    < button className = { styles.button } type = "submit"
    onClick={ getData } > Search < /button >
    < div className = { styles.grid_3_columns } >

    < /div >
      </form>
      </ div>
    </section>
  );
 }

