import React, { useState } from 'react';
import Axios from 'axios';
import Card from "../card/Card";
import styles from './Grid.module.css'

function Grid() {

const [inputText, setInput] = useState("");
const [post, setPost] = useState([]);
const [image, setImage] = useState('');

function handleChange(e) {
  console.log(e.target.value)
  setInput(e.target.value);
}

function sendInput(e) {
  e.preventDefault();

try {
  Axios.post("http://localhost:8000/sendInput", {
    inputText
  });
  Axios.get("http://localhost:3000/getImagesArray").then(response => {
      setPost(response.data.imagesArray);
  }, []);
} catch (error) {
  console.log(error.response.data);
}
}

function sendImage(e) {
  setImage(e.target.src);
  // console.log(e.target)
}

return <div className={styles.grid_2_columns}>
<Card imageTarget={image} />
<form onSubmit={handleChange}>
<div className={styles.searchBar}>
<input className={styles.input} type="text" onChange={handleChange} placeholder="Search..."
value={inputText}/>
<button type="submit" onClick={sendInput}>Search</button>
</div>
<div className = "grid-3-columns">
{post.map(url => {
  return <div key = {url.id} >
    <div>
    < img src = {url} alt = "images" onClick={sendImage} />
    </div>
    </div>
  })}
    </div>
</form>
  </div>
}

export default Grid;
