import React from 'react';
import Axios from 'axios';
import './Grid.module.css'


function SearchBar() {

const [inputText, setInput] = React.useState("");
const [post, setPost] = React.useState([]);

function handleChange(e) {
  console.log(e.target.value)
  setInput(e.target.value);
}

function sendInput(e) {
  e.preventDefault();

try {
  Axios.post("http://localhost:8000/sendInput", {
    inputText
  })

  React.useEffect(() => async () => {

  await Axios.get("http://localhost:3000/getImagesArray").then(response => {
      setPost(response.data.imagesArray);
    });
  }, []);
} catch (error) {
  console.log(error.response.data);
}
}




return <form onSubmit={handleChange}>
<input type="text" onChange={handleChange} placeholder="Search.."
value={inputText}/>
<button type="submit" onClick={sendInput}>Search</button>
{post.map(url => {
  return <div key = {url.id} >
    < img src = {url} alt = "images" / >
    </div> })}
</form>

}


export default SearchBar;
