import React, { useState } from 'react';
import Axios from 'axios';
import styles from './Mail.module.css';

function SendMail() {

const [email, setEmail] = useState("");

function handleChange(e) {
  setEmail(e.target.value);
}

async function sendNodemailer(e) {
  e.preventDefault();

await Axios.post('http://localhost:8001/nodemailer', {
  email
})
.then(response => {
  console.log(response);
})
.catch(error => {
  console.log(error)
})
}

return <div className={styles.mail_ctn}>
  <label className={styles.label}>Send to your friends: </label>
  <input className={styles.input} type="text" onChange={handleChange}
  placeholder="Enter Email" value={email} />
  <button className={styles.button} type="submit" onClick={sendNodemailer}>Send</button>
  </div>

}

export default SendMail;
