import React, { useRef, useState } from "react";
import Axios from "axios";
import PropTypes from "prop-types";
import styles from "./Mail.module.css";
// import ImgFile from "../imgFile/imgFile";

const SendMail = ({ FileInput, sendtoMail }) => {
  const formRef = useRef();
  // const emailRef = useRef();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [message, setMessage] = useState("");
  const [file, setFile] = useState({ fileURL: null });

  const data = {
    currentEmail: email,
    currentName: name,
    currentMessage: message,
    currentImage: sendtoMail || file.fileURL, // need check
  };

  // call backend mailer.js
  function sendNodemailer(e) {
    e.preventDefault();
    try {
      Axios.post("http://localhost:8001/sendNodemailer", data);
    } catch (error) {
      console.log(error.response.data);
    }
    alert("Your postcard has been sent!");
    formRef.current.reset();
  }

  const onFileChange = (file) => {
    console.log(file);
    setFile({
      fileURL: file.url,
    });
  };

  return (
    <form
      ref={formRef}
      className={styles.form}
      onSubmit={(e) => setEmail(e.target.value)}
    >
      <input
        // ref={emailRef}
        // className={styles.input}
        // type="text"
        // name="email"
        // placeholder="email"

        className={styles.input}
        type="text"
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        value={email}
        required
      />
      <input
        className={styles.input}
        type="text"
        onChange={(e) => setName(e.target.value)}
        placeholder="Name"
        value={name}
        required
      />
      <textarea
        className={styles.msg}
        type="text"
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Message"
        value={message}
        rows="5"
        cols="30"
        required
      />
      <div className={styles.btns}>
        <button
          className={styles.sendBtn}
          type="submit"
          onClick={sendNodemailer}
        >
          Send
        </button>
        <FileInput onFileChange={onFileChange} />
      </div>
    </form>
  );
};

SendMail.propTypes = {
  sendtoMail: PropTypes.string,
  FileInput: PropTypes.func,
};

export default SendMail;
